  char * makelist (const char * input, int type) {
    char * text = malloc (strlen(input) - 1);
    strncpy (text, &input[1], strlen (input) - 1);
    text[strlen (input) - 2] = '\0';
    char * s = strtok (text, " \t\v\n\f,");
    int listtype = 100;
    while (s) {
      char * dot = strchr (s, '.');
      var_t * var = varlookup (s, strlen(s) - (dot ? strlen(dot) : 0));
      if (!var) { // not a scalar or vector list, give up
	free (text);
	return NULL;
      }
      int vtype = var->type;
      while (dot) {
	vtype--;
	dot = strchr (dot+1, '.');
      }
      if (vtype < listtype)
	listtype = vtype;
      s = strtok (NULL, " \t\v\n\f,)");
    }
    if (listtype < type) { // incompatible list types
      free (text);
      return NULL;
    }      
    if (type >= 0)
      listtype = type;
    char * list = calloc (strlen("((scalar []){") + 1, sizeof (char));
    switch (listtype) {
    case scalar: strcpy (list, "((scalar []){"); break;
    case vector: strcpy (list, "((vector []){"); break;
    case tensor: strcpy (list, "((tensor []){"); break;
    default: assert (0); break;
    }
    strncpy (text, &input[1], strlen (input) - 1);
    text[strlen (input) - 2] = '\0';
    s = strtok (text, " \t\v\n\f,");
    while (s) {
      char * dot = strchr (s, '.');
      var_t * var = varlookup (s, strlen(s) - (dot ? strlen(dot) : 0));
      int vtype = var->type, si = 0;
      while (dot) {
	si += (dot[1] - 'x')*(vtype == 2 ? dimension : 1);
	vtype--;
	dot = strchr (dot+1, '.');
      }
      char member[80] = "";
      if (scope > 0 || var->i[0] < 0) { // dynamic allocation
	switch (listtype) {
	case scalar: {
	  switch (vtype) {
	  case 0: sprintf (member, "%s,", s); break;
	  case 1: {
	    int i, c;
	    for (i = 0, c = 'x'; i < dimension; i++, c++) {
	      char m[30];
	      sprintf (m, "%s.%c,", s, c);
	      strcat (member, m);
	    }
	    break;
	  }
	  case 2: {
	    int i, c;
	    for (i = 0, c = 'x'; i < dimension; i++, c++) {
	      int j, d;
	      for (j = 0, d = 'x'; j < dimension; j++, d++) {
		char m[30];
		sprintf (m, "%s.%c.%c,", s, c, d);
		strcat (member, m);
	      }
	    }
	    break;
	  }
	  default: assert (0);
	  }
	  break;
	}
	case vector: {
	  switch (vtype) {
	  case 1: {
	    sprintf (member, "{%s.x", s);
	    int i, c;
	    for (i = 1, c = 'y'; i < dimension; i++, c++) {
	      char m[30];
	      sprintf (m, ",%s.%c", s, c);
	      strcat (member, m);
	    }
	    strcat (member, "},");
	    break;
	  }
	  case 2: {
	    int i, c;
	    for (i = 0, c = 'x'; i < dimension; i++, c++) {
	      strcat (member, "{");
	      int j, d;
	      for (j = 0, d = 'x'; j < dimension; j++, d++) {
		char m[30];
		sprintf (m, "%s.%c.%c", s, c, d);
		strcat (member, m);
		if (j < dimension - 1)
		  strcat(member, ",");
	      }
	      strcat (member, "},");
	    }
	    break;
	  }
	  default: assert (0);
	  }
	  break;	  
	}
	case tensor: {
	  switch (vtype) {
	  case 2: {
	    int i, c;
	    strcat (member, "{");
	    for (i = 0, c = 'x'; i < dimension; i++, c++) {
	      strcat (member, "{");
	      int j, d;
	      for (j = 0, d = 'x'; j < dimension; j++, d++) {
		char m[30];
		sprintf (m, "%s.%c.%c", s, c, d);
		strcat (member, m);
		if (j < dimension - 1)
		  strcat(member, ",");
	      }
	      strcat (member, "}");
	      if (i < dimension - 1)
		strcat(member, ",");
	    }
	    strcat (member, "},");
	    break;
	  }
	  default: assert (0);
	  }
	  break;	  
	}
	default: assert(0);
	}
      }
      else { // static allocation
	int n = vtype - listtype, i;
	char * constant = var->constant ? "_NVARMAX + " : "";
	char coord[80] = "";
	int nd = 1;
	for (i = 0; i < n; i++)
	  nd *= dimension;
	for (i = 0; i < nd; i++) {
	  switch (listtype) {
	  case scalar:
	    sprintf (coord, "{%s%d},", constant, var->i[si+i]); break;
	  case vector: {
	    sprintf (coord, "{{%s%d}", constant, var->i[si+dimension*i]);
	    int j;
	    for (j = 1; j < dimension; j++) {
	      char s[80];
	      sprintf (s, ",{%s%d}", constant, var->i[si+dimension*i+j]);
	      strcat (coord, s);
	    }
	    strcat (coord, "},");
	    break;
	  }
	  case tensor: {
	    strcat (coord, "{");
	    int j, k, l = dimension*dimension*i;
	    for (j = 0; j < dimension; j++) {
	      strcat (coord, "{");
	      for (k = 0; k < dimension; k++) {
		char m[30];
		sprintf (m, "{%s%d}", constant, var->i[l++]);
		strcat (coord, m);
		if (k < dimension - 1)
		  strcat (coord, ",");
	      }
	      strcat (coord, "}");
	      if (j < dimension - 1)
		strcat (coord, ",");
	    }
	    strcat (coord, "},");
	    break;
	  }
	  default: assert (0);
	  }
	  strcat (member, coord);
	}
      }
      list = realloc (list, (strlen(list) + strlen(member) + 1)*sizeof(char));
      list = strcat (list, member);
      s = strtok (NULL, " \t\v\n\f,)");
    }
    free (text);
    char end[80]; // size should be OK at least up to dimension == 3
    switch (listtype) {
    case scalar: strcpy (end, "{-1}})"); break;
    case vector: {
      strcpy (end, "{{-1}");
      int i;
      for (i = 1; i < dimension; i++)
	strcat (end, ",{-1}");
      strcat (end, "}})");
      break;
    }
    case tensor: {
      strcpy (end, "{");
      int i, j;
      for (i = 0; i < dimension; i++) {
	strcat (end, "{{-1}");
	for (j = 1; j < dimension; j++)
	  strcat (end, ",{-1}");
	strcat (end, "}");
	if (i < dimension - 1)
	  strcat (end, ",");
      }
      strcat (end, "}})");
      break;
    }
    default: assert (0);
    }
    list = realloc (list, (strlen(list) + strlen(end) + 1)*sizeof(char));
    strcat (list, end);
    return list;
  }

  void new_field (var_t * var, char * block) {
    if (scope > 0) {
      // automatic variables
      fprintf (yyout, " new_%s%s%s%s(\"%s\"",
	       block ? "block_" : "",
	       var->constant ? "const_" : "",
	       var->symmetric ? "symmetric_" : 
	       var->face ? "face_" : 
	       var->vertex ? "vertex_" : 
	       "",
	       var->type == scalar ? "scalar" : 
	       var->type == vector ? "vector" : 
	       var->type == tensor ? "tensor" :
	       "internal_error",
	       var->v);
      if (block) {
	if (var->type == scalar)
	  fprintf (yyout, ", \"\"");
	fprintf (yyout, ", %s", block);
      }
      else if (var->constant) {
	if (var->type == scalar) {
	  fprintf (yyout, ", %d, %s", nconst, var->constant);
	  nconst++;
	}
	else if (var->type == vector) {
	  fprintf (yyout, ", %d, (%s [])%s", 
		   nconst, doubletype, var->constant);
	  nconst += dimension;
	}
	else
	  assert (0);
      }
      fputc (')', yyout);
    }
    else {
      // global constants and variables
      int * n = var->constant ? &nconst : &nvar;
      char * constant = var->constant ? "_NVARMAX + " : "";
      if (var->type == scalar) {
	fprintf (yyout, " {%s%d}", constant, (*n));
	var->i[0] = (*n)++;
      }
      else if (var->type == vector) {
	fputs (" {", yyout);
	int i;
	for (i = 0; i < dimension; i++) {
	  var->i[i] = (*n)++;
	  fprintf (yyout, "{%s%d}", constant, var->i[i]);
	  if (i < dimension - 1)
	    fputc (',', yyout);
	}
	fputc ('}', yyout);
      }
      else if (var->type == tensor) {
	int i, j, k = 0;
	for (i = 0; i < dimension; i++)
	  for (j = 0; j < dimension; j++) {
	    if (!var->symmetric || i <= j)
	      var->i[k] = (*n)++;
	    else
	      var->i[k] = var->i[j*dimension + i];
	    k++;
	  }
	fputs (" {", yyout);
	k = 0;
	for (i = 0; i < dimension; i++) {
	  fputc ('{', yyout);
	  for (j = 0; j < dimension; j++) {
	    fprintf (yyout, "{%s%d}", constant, var->i[k++]);
	    if (j < dimension - 1)
	      fputc (',', yyout);
	  }
	  fputc ('}', yyout);
	  if (i < dimension - 1)
	    fputc (',', yyout);
	}
	fputc ('}', yyout);
      }
      else
	assert (0);
    }
    if (!var->constant && var->maybeconst) {
      if (debug)
	fprintf (stderr, "%s:%d: '%s' cannot be a constant in this scope\n", 
		 fname, line, var->v);
      var->maybeconst = 0;
      var->wasmaybeconst = 1;
    }
  }

